	async init() {
		await super.init();
	}

	pluralize(num, s0, s1, s2) {
		if (num % 10 === 1 && num % 100 !== 11) {
			return s0;
		} else if (num % 10 >= 2 && num % 10 <= 4 && (num % 100 < 10 || num % 100 >= 20)) {
			return s1;
		} else {
			return s2;
		}
	}

